                            add(new CardData("Просыпаться", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Перевод</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Просыпаться</font></h2>", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Present Simple</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Wake [weik]</font></h2><h3 style=\"text-align: center;\"><font face=\"Open Sans\" color=\"#ffffff\">Past Simple</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Woke [wouk]</font></h2><h3 style=\"text-align: center;\"><font color=\"#ffffff\" face=\"Open Sans\">Past Participle</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Woken ['wouk(e)n]</font></h2>"));
