                            add(new CardData("Плакать", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Перевод</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Плакать</font></h2>", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Present Simple</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Weep [wi:p]</font></h2><h3 style=\"text-align: center;\"><font face=\"Open Sans\" color=\"#ffffff\">Past Simple</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wept [wept]</font></h2><h3 style=\"text-align: center;\"><font color=\"#ffffff\" face=\"Open Sans\">Past Participle</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wept [wept]</font></h2>"));
                            add(new CardData("Мочить", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Перевод</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Мочить</font></h2>", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Present Simple</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Wet [wet]</font></h2><h3 style=\"text-align: center;\"><font face=\"Open Sans\" color=\"#ffffff\">Past Simple</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wet [wet]</font></h2><h3 style=\"text-align: center;\"><font color=\"#ffffff\" face=\"Open Sans\">Past Participle</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wet [wet]</font></h2>"));
