                            add(new CardData("Извиваться", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Перевод</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Извиваться</font></h2>", "<h3 style=\"text-align: center; \"><font face=\"Open Sans\" color=\"#ffffff\">Present Simple</font></h3><h2 style=\"text-align: center; \"><font face=\"Open Sans\">Wind [waind]</font></h2><h3 style=\"text-align: center;\"><font face=\"Open Sans\" color=\"#ffffff\">Past Simple</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wound [waund]</font></h2><h3 style=\"text-align: center;\"><font color=\"#ffffff\" face=\"Open Sans\">Past Participle</font></h3><h2 style=\"text-align: center;\"><font face=\"Open Sans\">Wound [waund]</font></h2>"));
